-- 导出 ym365 的数据库结构
CREATE DATABASE IF NOT EXISTS `ym365` /*!40100 DEFAULT CHARACTER SET utf8 */;
USE `ym365`;


-- 导出  表 ym365.ym_article 结构
CREATE TABLE IF NOT EXISTS `ym_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `title` varchar(50) NOT NULL COMMENT '文章名字',
  `category` int(1) NOT NULL COMMENT '文章分类',
  `content` text NOT NULL COMMENT '文章内容',
  `status` int(1) NOT NULL DEFAULT '0' COMMENT '是否发布，0为未发布，1为发布',
  `created_at` varchar(15) NOT NULL COMMENT '创建时间',
  `updated_at` varchar(15) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=76 DEFAULT CHARSET=utf8 COMMENT='文章表';

